// Global CSS module declarations for TypeScript
declare module '*.css' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.scss' {
  const classes: { [key: string]: string };
  export default classes;
}

declare module '*.sass' {
  const classes: { [key: string]: string };
  export default classes;
}

// Specific module declarations for Leaflet CSS
declare module 'leaflet/dist/leaflet.css';